import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { FormData } from '../../models/FormData';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-form',
  templateUrl: './form.component.html',
  styleUrls: ['./form.component.scss'],
})
export class FormComponent implements OnInit {
  public formData: Array<FormData> = [
    { label: 'Nome Completo', name: 'name', placeholder: 'nome' },
    { label: 'CPF', name: 'legalId', placeholder: '000.000.000-00' },
    { label: 'Email', name: 'email', placeholder: 'example@gmail.com' },
    { label: 'Telefone', name: 'phoneNumber', placeholder: '(99) 9 9999-9999' },
    { label: 'CEP', name: 'postalCode', placeholder: '00000-00' },
    { label: 'Estado', name: 'state', placeholder: 'estado' },
    { label: 'Cidade', name: 'city', placeholder: 'cidade' },
    { label: 'Bairro', name: 'neighborhood', placeholder: 'bairro' },
    { label: 'Rua', name: 'street', placeholder: 'rua' },
    { label: 'Número', name: 'addressNumber', placeholder: 'número' },
    { label: 'Complemento', name: 'complement', placeholder: 'complemento' },
  ];

  public formRegister: FormGroup = this.formBuilder.group({
    name: ['', Validators.required],
    legalId: ['', Validators.required],
    email: ['', Validators.required],
    phoneNumber: ['', Validators.required],
    postalCode: ['', Validators.required],
    state: ['', Validators.required],
    city: ['', Validators.required],
    neighborhood: ['', Validators.required],
    street: ['', Validators.required],
    addressNumber: ['', Validators.required],
    complement: [''],
  });

  public showErrors: boolean = false;
  public isLoading: boolean = false;
  public isSubmitting: boolean = false;
  public showModal: boolean = false;

  constructor(private formBuilder: FormBuilder, private http: HttpClient) {}

  ngOnInit(): void {}

  submitForm(): void {
    if (this.formRegister.valid) {
      this.http.post('');
      this.showErrors = false;
      console.log(this.formRegister);
    }
    this.showErrors = true;
  }
}
<div class="container">
  <h2 class="title">Cadastro de Usuário</h2>
  <form (submit)="submitForm()" [formGroup]="formRegister">
    <h5 class="form-subtitle">Informações Pessoais</h5>
    <div class="form-box">
      <div
        *ngFor="let item of formData"
        class="form-group"
        [ngClass]="{ 'postal-code': item.name === 'postalCode' }"
      >
        <label>{{ item.label }}<span class="error-message">*</span></label>
        <input
          class="form-control"
          type="text"
          formControlName="{{ item.name }}"
          placeholder="{{ item.placeholder }}"
        />
        <p
          *ngIf="showErrors && formRegister.get(item.name)?.errors"
          class="error-message"
        >
          O campo {{ item.label }} é obrigatorio
        </p>
      </div>
    </div>
    <button type="submit" class="btn">Criar conta</button>
  </form>
  <app-modal *ngIf="showModal"></app-modal>
</div>
